document.addEventListener("DOMContentLoaded",function(){initializeChips()});
function initializeChips(){var a=document.querySelectorAll(".chips");window.instances=M.Chips.init(a,{placeholder:"Add your first tag...",secondaryPlaceholder:"Add another tag...",data:currentTags.slice(0),onChipAdd:function(b,a){b=a.innerText.substr(0,a.innerText.lastIndexOf("close"));postNewTag(b)},onChipDelete:function(a,c){a=c.innerText.substr(0,c.innerText.lastIndexOf("close"));confirm("Are you sure you want to delete the '"+a+"' tag? You cannot reverse this action.")?deleteTag(a):initializeChips()}})}
function postNewTag(a){console.log(a);currentTags=window.instances[0].chipsData.slice(0)}function deleteTag(a){console.log(a);currentTags=window.instances[0].chipsData.slice(0)};
